package Operators;

public class Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			      int x = 30;
			      int y = 60;
			      
			      System.out.println("x < y = " + (x < y) );
			      System.out.println("y <= x = " + (y <= x) );
			      System.out.println("x > y = " + (x > y) );
			      System.out.println("y >= x = " + (y >= x) );
			   }
			}
	